            svgHeight: 0,
            rinkLengthInPixel: 0,
            rinkWidthInPixel: 300,
            hogLine1: 0,
            hogLine2: 0,
        };
    },
    mounted() {

        this.initSvg();
        this.calculateRinkDimensions();
        this.drawRink();
        this.replay(this.trajectoryData);
    },
    methods: {
        initSvg() {
            this.svgWidth = document.getElementById('preview').clientWidth;
            this.svgHeight = document.getElementById('preview').clientHeight;

            d3.select("#trajectorySVG").attr("viewBox", `0 0 ${this.svgWidth} ${this.svgHeight}`);
        },
        calculateRinkDimensions() {
            this.rinkLengthInPixel = 300 * (this.rinkLength / this.rinkWidth);
            this.hogLine1 = this.yScale(10); // hogLine  10
            this.hogLine2 = this.rinkLengthInPixel - this.yScale(10); // hogLine 
        },
        xScale(value) {
            return d3.scaleLinear().domain([0, this.rinkWidth]).range([0, this.svgWidth])(value);
        },
        yScale(value) {
            return d3.scaleLinear().domain([0, this.rinkLength]).range([0, this.rinkLengthInPixel])(value);
        },
        drawRink() {
            const canvas = d3.select("#canvas").attr("class", "canvas").attr("transform", `translate(0, ${this.svgHeight}) scale(1, -1)`);

            const rect = canvas.insert("rect", ":first-child")
                .attr("fill", "#e9e9e9")
                .attr("x", 0)
                .attr("y", 0)
                .attr("stroke", "#333")
                .attr("width", this.rinkWidthInPixel) // 
                .attr("height", this.rinkLengthInPixel); // 

            // Hog Line 1
            canvas.insert("line", () => rect.node().nextSibling)
                .attr("x1", 0)
                .attr("y1", this.hogLine1)
                .attr("x2", this.svgWidth)
                .attr("y2", this.hogLine1)
                .attr("stroke", "red")
                .attr("stroke-width", 1);

            // Hog Line 2
            canvas.insert("line", () => rect.node().nextSibling)
                .attr("x1", 0)
                .attr("y1", this.hogLine2)
                .attr("x2", this.svgWidth)
                .attr("y2", this.hogLine2)
                .attr("stroke", "green")
                .attr("stroke-width", 1);


        },

        payFrame(point) {
            this.$refs.curlingStone1.interpolatePlayFrame(point, null)
        },


        replay(trajectoryData) {
            if (this.index >= trajectoryData.length) return;
            const point = trajectoryData[this.index];
            const pointTrans = {... point}
            pointTrans.x = this.xScale(point.x)
            pointTrans.y = this.yScale(point.y)

            this.$refs.curlingStone1.interpolatePlayFrame(pointTrans, () => {
                ++this.index;
                this.replay(trajectoryData);
            });
        },

        dealZoom(status) { // status= in , out
            if (status == 'in') {
                d3.select(".canvas").attr("transform", `translate(${this.svgWidth / 4}, ${this.svgHeight / 2}) scale(0.5, -0.5)`);
            } else {
                d3.select(".canvas").attr("transform", `translate(0, ${this.svgHeight}) scale(1, -1)`);
            }
        },

        dealFrameEnd(stage, point) { // stage = inHogLines, beforeHogLine1, afterHogLine2

            if (stage == 'inHogLines') {
                d3.select("#trajectorySVG").attr("viewBox", `0 ${-(point.y / 2)} ${this.svgWidth} ${this.svgHeight}`);
            } else if (stage == 'beforeHogLine1') {
                d3.select("#trajectorySVG").attr("viewBox", `0 0 ${this.svgWidth} ${this.svgHeight}`);
            } else {
                d3.select("#trajectorySVG").attr("viewBox", `0 ${this.svgHeight - this.rinkLengthInPixel} ${this.svgWidth} ${this.svgHeight}`);
            }
        }
